sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if ( newValue === '') {
		return;
	}
	// Create glide form variable
	var whatneeded = g_form.getValue('u_what_needed');

	// Clear all options from what needed list

	g_form.clearOptions('u_what_needed');

	//if the value of the Request type is 'hr', add
	//two hr choices along with other.
	if(newValue=='hr'){
		g_form.addOption('u_what_needed','hr1', 'Human Resource1');
		g_form.addOption('u_what_needed', 'hr2', 'Human Resource2');
		g_form.addOption(('u_what_needed','other','Other'));
	}

	//if the value of the Request Type is facilites, add
	//tow facilities choices along with other

	if(newValue == 'facilities'){

	g_form.addOption('u_what_needed','facilities1','Facilities1');
	g_form.addOption('u_what_needed','facilities2','Facilities2');
	g_form.addOption('u_what_needed','other','Other');
	}

	//if the value of the Request type is Legal, add
	// two Legal choices along with Other
	if(newValue=='legal'){
	g_form.addOption('u_what_needed','legal1','Legal1');
	g_form.addOption('u_what_needed','legal2','Legal2');
	g_form.addOption('u_what_needed','other','Other');
	}
	//if the form is loading and it is not new record, set u_what_needed value to the
	// value from the record before it was loaded

	if(isLoading && !g_form.isNewRecord()){
		g_form.setValue('u_what_needed', whatneeded);
	}





	//Type appropriate comment here, and begin script below

}
